import React,{useState} from "react";
import ExpenseForm from "./ExpenseForm";
import './NewExpense.css'
const NewExpense = (props) =>
{
        const saveExpenseDataHandler = (enteredExpenseData) => {
            const expenseData = {
                ...enteredExpenseData,
                id: Math.random().toString()
            }
            props.onAddExpense(expenseData)
        }
    const [expenseFormShower,setexpenseFormShower]=useState(<p></p>)
    let visibleButton = true

    const [buttonHider, setButtonHider]  = useState(visibleButton)
        const formShower = () =>
        {
            setexpenseFormShower(<ExpenseForm onSaveExpenseData={saveExpenseDataHandler}/>)
            setButtonHider(false)

        }
        const buttonCode = <button onClick={formShower}>Add Expense</button>
    return (
        <div className="new-expense">
            {buttonHider && buttonCode}

            {/*<ExpenseForm onSaveExpenseData={saveExpenseDataHandler}/>*/}
            {expenseFormShower}
        </div>
    )

}
export default NewExpense
